const response = $input.first().json;
let message = response.response || 'Sorry, I could not process that. Please try again!';

// Clean up action markers from AI response for Telegram
message = message.replace(/\[ACTION:[^\]]+\]/g, '');
message = message.replace(/\[SEARCH_GIGS\]/g, '');
message = message.replace(/\[POST_GIG\]/g, '');
message = message.replace(/\[REGISTER_FREELANCER\]/g, '');

// Add action results if any
if (response.action_result) {
  const ar = response.action_result;
  if (ar.type === 'GIG_POSTED') {
    message += `\n\nâœ… **Gig Posted Successfully!**\nTitle: ${ar.title}\nID: ${ar.gig_id}`;
  } else if (ar.type === 'FREELANCER_REGISTERED') {
    message += `\n\nâœ… **You are now a freelancer!**\nCategories: ${ar.categories.join(', ')}`;
  } else if (ar.type === 'GIGS_FOUND' && ar.gigs && ar.gigs.length > 0) {
    message += '\n\nðŸ” **Found Gigs:**\n';
    ar.gigs.forEach((g, i) => {
      message += `${i+1}. ${g.title} - $${g.budget_min}-$${g.budget_max} (${g.location})\n`;
    });
  }
}

// Truncate if too long for Telegram
if (message.length > 4000) {
  message = message.substring(0, 3997) + '...';
}

return [{ message: message }];
